import React,{useEffect, useState} from 'react'
import useSolicitar from '../hooks/useSolicitar';
import productos from '../produtos.json'



const Detalle = ({match}) =>{

    const id = match.params.id

    const pinturas = productos.filter((item)=>(item.id==id))

    console.log(id)
    console.log(pinturas)
    return (
        <div>
           {pinturas.map((pintura)=>(
               <div>
                   <img src = {pintura.img}/>
                   <p>{pintura.detalle}</p>
                   <input type = "text" placeholder = "Cantidad"></input>
                   <button type="submit"></button>
               </div>
           ))}
        </div>
    )
}

export default Detalle
