import React,{useEffect, useState} from 'react'
import useSolicitar from '../hooks/useSolicitar';
import productos from '../produtos.json'



const Detalle = (match) =>{

    const prods = useSolicitar()
    const [id,setID] = useState(null)
    
    setID(match.match.params.id)
    return (
        <div>
           {productos.filter((item)=>item.id === id )}
        </div>
    )
}

export default Detalle
