import React,{useState,useEffect} from 'react'
import productos from '../produtos.json'
import Card from '../components/card'
import { withRouter } from 'react-router-dom';

const Listado = () =>{
    const [cantidad,setCantidad] = useState([])
    const [nombre,setNombre] = useState([])
    const [pedido,setPedido] = useState([])
    const [compra,setCompra] = useState([])

    const agregarPedidos = e => {
        e.preventDefault()
        // this.props.history.push('/pedido')
        // this.props.history.push('/pedido',this.state)
               
        // console.log("cantidad",cantidad)
        // console.log("nombre",nombre)        
  
   
        // setPedido([...pedido,
        //     compra])

        setPedido([...pedido,
        {name:nombre,cantidad}])
        setCompra([...compra,
            {
            name: nombre,
            cantidad
            }
         ])
 
        console.log(nombre)
    }
 
    const obtenerCantidad = e =>{
        
        setNombre(e.target.name)
        setCantidad(...cantidad,e.target.value)
       
    }


    return (
        
        <div className = "container-fluid">
            <h1>Listado de productos</h1>

            <form onSubmit = {agregarPedidos}>
                <div className = "row" >
                   
                    {/* Recorro el Json para listar los productos */}
                   
                    {productos.map((datos)=>
                    <div>
                        <Card data = {datos}
                        onChange = { obtenerCantidad }
                        
                        />
                   
                    </div>)
                    }  

                </div>    

                <div className = "row">
                    <button
                        type = "submit"
                        className = "btn btn-primary">
                        Solicitar
                    </button>                     
                </div>    
            </form>
            <ul>
                {compra.map((compras)=>(<li>{compras.name}</li>))}
            </ul>
        </div>

    )
}


export default withRouter(Listado)
