import React,{useState,useEffect} from 'react'
import productos from '../produtos.json'
import Card from '../components/card'
import { withRouter, Redirect } from 'react-router-dom';
import Pedido from '../pages/pedidos'


const Listado = () =>{
    const [nombre,setNombre] = useState([])
 

    handleSubmit = e => {
        e.preventDefault()
        this.props.history.push('/pedido')
        // this.props.history.push('/pedido',this.state)
        
    }
  
    return (
        
        <div className = "container-fluid">
            <h1>Listado de productos</h1>
            <form onSubmit = {()=>handleSubmit()}>
                <div className = "row" >
                    {productos.map((datos)=>{

                        return <Card 
                        data = {datos} 
                        
                        handleChange = {()=>handleChange()}/>
                        
                    })}             
                </div>    

                <div className = "row">
                    <button
                        type = "submit"
                        className = "btn btn-primary">
                        Solicitar
                    </button>                     
                </div>    
            </form>
            <Pedido state = {this.state} data = {productos} />
        
        </div>

    )
}


export default withRouter(Listado)
