        name: nombre,
        cantidad
        })
    }


    return (
        
        <div className = "container-fluid">
            <h1>Listado de productos</h1>

            <form onSubmit = {agregarPedidos}>
                <div className = "row" >
                   
                    {/* Recorro el Json para listar los productos */}
                   
                    {productos.map((datos)=>
                    <div>
                        <Card data = {datos}
                        onChange = {e=>(setCantidad(e.target.value),setNombre({...nombre,name:e.target.name})) }
                        
                        />
                   
                    </div>)
                    }  

                </div>    

                <div className = "row">
                    <button
                        type = "submit"
                        className = "btn btn-primary">
                        Solicitar
                    </button>                     
                </div>    
            </form>
            <ul>
                {pedido.map((pedidos)=>(<li>{pedidos.name} {pedidos.cantidad}</li>))}
            </ul>
        </div>

    )
}


export default withRouter(Listado)
