        // setCompra([...compra,
        //     {
        //     name: nombre,
        //     cantidad
        //     }
        //  ])
 
        // console.log(nombre)
    }
 
    const obtenerCantidad = e =>{
        
        // setNombre(e.target)
        setCantidad(e.target.value)
        setNombre(e.target.name)
        const arrayCompra = [...compra,
            compra.map((compras)=>(
                compras.name===nombre ? cantidad:cantidad 
            ))
        ]
        setCompra(arrayCompra)
 
    }


    return (
        
        <div className = "container-fluid">
            <h1>Listado de productos</h1>

            <form onSubmit = {agregarPedidos}>
                <div className = "row" >
                   
                    {/* Recorro el Json para listar los productos */}
                   
                    {productos.map((datos)=>
                    <div>
                        <Card data = {datos}
                        onChange = { obtenerCantidad }
                        
                        />
                   
                    </div>)
                    }  

                </div>    

                <div className = "row">
                    <button
                        type = "submit"
                        className = "btn btn-primary">
                        Solicitar
                    </button>                     
                </div>    
            </form>
            <ul>
                {/* {compra.map((compras)=>(<li>{compras.name}</li>))} */}
            </ul>
        </div>

    )
}


export default withRouter(Listado)
