import React from 'react'
import productos from '../produtos.json'
import Card from '../components/card'
import { withRouter, Redirect } from 'react-router-dom';
import Pedido from '../pages/pedidos'

class Listado extends React.Component{
    state = {
        nombre:[],
        cantidad:[]
        
    }

    handleSubmit = e => {
        e.preventDefault()
        this.props.history.push('/pedido')
        // this.props.history.push('/pedido',this.state)
        
        
        return <Pedido state = {this.state} />
    }
    handleChange = e =>{
      
        this.setState({
             
            nombre:[...this.state.nombre, e.target.name],
            cantidad: [...this.state.nombre, e.target.value]
        })
        console.log(this.state)
       
    }
    render(){

    return (
        
        <div className = "container-fluid">
            <h1>Listado de productos</h1>
            <form onSubmit = {this.handleSubmit}>
                <div className = "row" >
                    {productos.map((datos)=>{

                        return <Card 
                        data = {datos} 
                        
                        handleChange = {this.handleChange}/>
                        
                    })}             
                </div>    

                <div className = "row">
                    <button
                        type = "submit"
                        className = "btn btn-primary">
                        Solicitar
                    </button>                     
                </div>    
            </form>
            <Pedido state = {this.state} data = {productos} />
        
        </div>

    )
                }
}

export default withRouter(Listado)
