import React,{useEffect, useState} from 'react'
import useSolicitar from '../hooks/useSolicitar';
import productos from '../produtos.json'



const Detalle = ({match}) =>{

    const id = match.params.id

    const pinturas = productos.filter((item)=>(item.id==id))
    return (
        <div className = "container">
           {pinturas.map((pintura)=>(
               <div key = {pintura.nombre}>

                  
                   <p>{pintura.detalle}</p>
                   
                   <div> 
                       <img src = {pintura.img}/>
                    </div>
                   <div>
                       <form>

                       </form>
                   </div>
               </div>
               
           ))}
        </div>
    )
}

export default Detalle
